package ControlFlowStatements.DecesionMaking_Statements;

public class Switch_statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		
		switch(a) {
		case 0:
			System.out.println("The num is 0");//index 0
			break;
		
		case 1:
			System.out.println("The num is 1");//index 1
			break;
			
		case 2:
			System.out.println("The num is 2");
			break;
			
		case 3:
			System.out.println("The num is 3");
			break;
		
		default:
			System.out.println("None of the above");
		
		}
				
	}

}
